import React, { useState, useEffect } from 'react'
import { } from 'react-router-dom'
import { Col, Row, Table, } from 'reactstrap'
import { Button, Form, FormGroup, Input } from 'reactstrap';
// Viewmodel types
const Ingredient = (name, typeId) => { return { Name: name, TypeId: typeId } }

const Inventory = () => {
    const [newIngName, setNewIngName] = useState("");
    const [newIngCatId, setNewIngCatId] = useState("1");

    const [savedIngredients, setIngItems] = useState(null); // Ingredient[]

    const handleSubmit = (evt) => {
        evt.preventDefault();
        // FakeAjax
        setTimeout(() => {
            const newIng = Ingredient(newIngName, newIngCatId)
            console.log('Inventory save new item ' + newIng)
            const newSavedIngredients = [...savedIngredients, newIng];
            setIngItems(newSavedIngredients);
            localStorage.setItem('savedIngredients', JSON.stringify(newSavedIngredients))
        }, 0);
    }

    useEffect(() => {
        console.log("Inventory useEffect[] started")
        // FakeAjax
        setTimeout(() => {
            const json = localStorage.getItem("savedIngredients")
            const obj = json ? Object.assign([], JSON.parse(json)) : []
            setIngItems(obj);
        }, 1000);
    }, [])

    return (
        <div>
            <h1>Inventory</h1>
            <br />
            <Row>
                <Col>
                    <h3>Your Inventory</h3>
                    <br /><br />
                    <Table>
                        <thead>
                            <tr><th>Item</th><th></th></tr>
                        </thead>
                        <tbody>
                            {!savedIngredients ? <tr><td colSpan="2">loading</td></tr>
                                : savedIngredients.length === 0 ? <tr><td> colSpan="2"None</td></tr>
                                    : savedIngredients.map((ing, i) => 
                                        <tr key={i}>
                                            <td>{ing.Name}</td>
                                            <td><a onClick={() => console.log('remove ' + i)}>remove</a></td>
                                        </tr>
                                    )}
                        </tbody>
                    </Table>
                </Col>
                <Col>
                    <h3>Add a new Inventory Item</h3>
                    <br /><br />
                    <Form onSubmit={handleSubmit}>
                        <FormGroup>
                            <Input
                                type="text"
                                placeholder="New Ingredient Name (leave blank for random)"
                                value={newIngName}
                                onChange={e => setNewIngName(e.target.value)} />
                        </FormGroup>
                        <FormGroup>
                            <Input
                                type="select"
                                value={newIngCatId}
                                onChange={e => setNewIngCatId(e.currentTarget.value)}>
                                <option value="1">Normal Spirit</option>
                                <option value="2">Overproof Spirit</option>
                                <option value="3">Liquote/Cordial</option>
                                <option value="4">Fruit Juice</option>
                                <option value="5">Bitters/Spices</option>
                            </Input>
                        </FormGroup>
                        <Button>Submit</Button>
                    </Form>
                </Col>
            </Row>
        </div>
    )
}

export default Inventory
