import React, { useState, useEffect } from 'react'
import { } from 'react-router-dom'
import { Container, Row, Col, Form, FormGroup, Input } from 'reactstrap'
import { GenerateGuid } from '../Library'

const CreateRecipe = () => {
    const generateIngredientEntry = () => { return { value: null, id: GenerateGuid() } }
    const [recipeName, setRecipeName] = useState("");
    const [recipeInstructions, setRecipeInstructions] = useState("");
    const [recipeIngredients, setRecipeIngredients] = useState([generateIngredientEntry()])

    useEffect(() => {
        console.log("Create Recipe useEffect[] started")
        // FakeAjax
        /* setTimeout(() => {
            const json = localStorage.getItem("savedIngredients")
            const obj = json ? Object.assign([], JSON.parse(json)) : []
            setSavedIngredients(obj);
        }, 1000); */
    }, [])

    const handleIngredientValueChange = (id, event) => {
        const ingredients = [...recipeIngredients];
        ingredients.filter(ing => ing.id === id)[0].value = event.target.value;
        setRecipeIngredients(ingredients);
    }

    return (
        <div>
            <h1>Create a new Cocktail Recipe</h1>
            <br />
            <Container>
                <Row>
                    <Col xs="6">
                        <Form>
                            <FormGroup>
                                <Input
                                    type="text"
                                    placeholder="New Recipe Name (leave blank for random)"
                                    value={newRecipeName}
                                    onChange={e => setNewRecipeName(e.target.value)} />
                            </FormGroup>
                            <FormGroup>
                                <Input
                                    type="textarea"
                                    placeholder="Instructions (optional)"
                                    value={newRecipeInstructions}
                                    onChange={e => setNewRecipeInstructions(e.target.value)} />
                            </FormGroup>
                            <div>
                                {recipeIngredients.map(ing =>
                                    <div key={ing.id}>
                                        <Input 
                                            type="text"
                                            value={ing.value}
                                            onChange={e => handleIngredientValueChange(ing.id, e)} />
                                    </div>
                                )}
                            </div>
                        </Form>
                    </Col>
                </Row>
            </Container>
        </div>
    )
}

export default CreateRecipe
