import React, { useState, useEffect } from 'react'
import { } from 'react-router-dom'
import { Col, Row, Table, } from 'reactstrap'
import { Button, Form, FormGroup, Input } from 'reactstrap';
//import { LocalStorageFetch } from '.././Library'
// Viewmodel types
const Ingredient = (name, typeId) => { return { Name: name, TypeId: typeId} }

const Model = {  }


const Inventory = () => {
    const [newIngName, setNewIngName] = useState("");
    const [newIngCatId, setNewIngCatId] = useState("1");

    const [savedIngredients, setIngItems] = useState([])

    const handleSubmit = (evt) => {
        evt.preventDefault();
        // FakeAjax
        setTimeout(() => {

            const newIng = Ingredient(newIngName, newIngCatId)
            console.log('save new item ' + newIng)
            const newSavedIngredients = [...savedIngredients, newIng];
            console.log(newSavedIngredients)
            console.log(JSON.stringify(newSavedIngredients))
            // [{"Name":"Rum","TypeId":"1"},{"Name":"Whiskey","TypeId":"1"},{"Name":"Foooo","TypeId":"1"}]
            setIngItems(newSavedIngredients);
            localStorage.setItem('savedIngredients', JSON.stringify(newSavedIngredients))
            console.log(localStorage.getItem('savedIngredients'))
       }, 0);
    }

    useEffect(() => {
        console.log("Inventory useEffect[] started")
        // FakeAjax
        setTimeout(() => {
            const json = localStorage.getItem("savedIngredients")
            const obj = json ? Object.assign({}, JSON.parse(json)) : [Ingredient("Rum", "1"), Ingredient("Whiskey", "1")]
            console.log("Obj from localStorage next")
            console.log(json)
            setIngItems(false ? obj : [Ingredient("Rum", "1"), Ingredient("Whiskey", "1")]);
        }, 2000);
    }, [])

    return (
        <div>
            <h1>Inventory</h1>
            <br />
            <Row>
                <Col>
                    <h3>Your Inventory</h3>
                    <br /><br />
                    <Table>
                        <thead>
                            <tr><th>Item</th></tr>
                        </thead>
                        <tbody>
                            {!savedIngredients || savedIngredients.length === 0
                                ? <tr><td>loading</td></tr>
                                : savedIngredients.map(_ => { return <tr key={_.Name}><td>{_.Name}</td></tr> })}
                        </tbody>
                    </Table>
                </Col>
                <Col>
                    <h3>Add a new Inventory Item</h3>
                    <br /><br />
                    <Form onSubmit={handleSubmit}>
                        <FormGroup>
                            <Input type="text" id="newIngredName" placeholder="New Ingredient Name (leave blank for random)"
                                value={newIngName}
                                onChange={e => setNewIngName(e.target.value)} />
                        </FormGroup>
                        <FormGroup>
                            <Input type="select" name="select" id="newIngredType"
                                value={newIngCatId}

                                onChange={e => setNewIngCatId(e.currentTarget.value)}>
                                <option value="1">Normal Spirit</option>
                                <option value="2">Overproof Spirit</option>
                                <option value="3">Liquote/Cordial</option>
                                <option value="4">Fruit Juice</option>
                                <option value="5">Bitters/Spices</option>
                            </Input>
                        </FormGroup>
                        <Button>Submit</Button>
                    </Form>
                </Col>
            </Row>
        </div>
    )
}

export default Inventory
