import React, { useState } from 'react'
import { } from 'react-router-dom'
import { } from 'reactstrap'
import { GenerateGuid } from '../Library'

const Recipes = () => {

    const [savedRecipes, setSavedRecipes] = useState(null); // GetSampleRecipe()[]

    useEffect(() => {
        console.log("Recipes useEffect[] started")
        // FakeAjax
        setTimeout(() => {
            const json = localStorage.getItem("savedRecipes")
            const obj = json ? Object.assign([], JSON.parse(json)) : []
            setSavedRecipes(obj);
        }, 1000);
    }, [])

    return (
        <div>
            <h1>Your Collection of Recipes</h1>
            <br />
            <Table>
                <thead>
                    <tr><th>Recipe Name</th><th></th></tr>
                </thead>
                <tbody>
                    {!savedRecipes ? <tr><td colSpan="2">loading</td></tr>
                        : savedRecipes.length === 0 ? <tr><td colSpan="2">None</td></tr>
                            : savedRecipes.map(recipe =>
                                <tr key={recipe.id}>
                                    <td>{recipe.name}</td>
                                    <td>{recipe.ingredients.map(ingredient =>
                                        <span key={ingredient.id}>
                                            {ingredient.quantity} {ingredient.name}
                                        </span>
                                    )}</td>
                                </tr>
                            )}
                </tbody>
            </Table>
        </div>
    )
}

export default Recipes
