const CreateRecipe = () => {
    const [newRecipeName, setNewRecipeName] = useState("");
    const [newRecipeInstructions, setNewRecipeInstructions] = useState("");

    useEffect(() => {
        console.log("Create Recipe useEffect[] started")
        // FakeAjax
        setTimeout(() => {
            const json = localStorage.getItem("savedIngredients")
            const obj = json ? Object.assign([], JSON.parse(json)) : []
            setIngItems(obj);
        }, 1000);
    }, [])

    return (
        <div>
            <h1>Create a new Cocktail Recipe</h1>
            <br />
            <Container>
                <Row>
                    <Col xs="6">
                        <Form>
                            <FormGroup>
                                <Input
                                    type="text"
                                    placeholder="New Recipe Name (leave blank for random)"
                                    value={newRecipeName}
                                    onChange={e => setNewRecipeName(e.target.value)} />
                            </FormGroup>
                            <FormGroup>
                                <Input
                                    type="textarea"
                                    placeholder="Instructions (optional)"
                                    value={newRecipeInstructions}
                                    onChange={e => setNewRecipeInstructions(e.target.value)} />
                            </FormGroup>
                        </Form>
                    </Col>
                </Row>
            </Container>
        </div>
    )
}

export default CreateRecipe
