import React, { useState, useEffect } from 'react'
import { Link } from 'react-router-dom'
import { Container, Col, Row, Table, ListGroupItemHeading } from 'reactstrap'
import { Button, Form, FormGroup, Label, Input, FormText } from 'reactstrap';

const Inventory = () => {
    const [newIngName, setNewIngName] = useState("");
    const [newIngCatId, setNewIngCatId] = useState("1");

    const [itemsAreLoaded, itemsAreLoadedSet] = useState(false)
    const [items, itemsSet] = useState([])

    const handleSubmit = (evt) => {
        evt.preventDefault();
        alert(`Submitting Name ${newIngName} Value ${newIngCatId}`)
    }

    const g = <span>Hello!</span>
    const f = <a />

    const GetG = () => {
        return g;
    }

    useEffect(() => {
        console.log("Inventory useEffect[] started")
        // do the thing then
        itemsAreLoadedSet(false)
        itemsSet([{ IngName: "Scotch"}, {IngName: "Bourbon"}]);
    }, [])

    return (
        <div>
            <h1>Inventory {GetG()} <a onClick={() => console.log(items)}>Tap</a></h1>
            <br /><br />

            <Row>
                <Col>
                    <h3>Your Inventory</h3>
                    <br /><br />
                    <Table>
                        <thead>
                            <tr><th>Item</th></tr>
                        </thead>
                        <tbody>
                            {!itemsAreLoaded ? <tr><td>loading</td></tr> 
                                : items.map(_ => {return <tr><td>${_.IngName}</td></tr>})}
                        </tbody>
                    </Table>
                </Col>
                <Col>
                    <h3>Add a new Inventory Item</h3>
                    <br /><br />
                    <Form onSubmit={handleSubmit}>
                        <FormGroup>
                            <Input type="text" id="newIngredName" placeholder="New Ingredient Name (leave blank for random)"
                                value={newIngName}
                                onChange={e => setNewIngName(e.target.value)} />
                        </FormGroup>
                        <FormGroup>
                            <Input type="select" name="select" id="newIngredType"
                                value={newIngCatId}

                                onChange={e => setNewIngCatId(e.currentTarget.value)}>
                                <option value="1">Normal Spirit</option>
                                <option value="2">Overproof Spirit</option>
                                <option value="3">Liquote/Cordial</option>
                                <option value="4">Fruit Juice</option>
                                <option value="5">Bitters/Spices</option>
                            </Input>
                        </FormGroup>
                        <Button>Submit</Button>
                    </Form>
                </Col>
            </Row>
        </div>
    )
}

export default Inventory
